import React from 'react'
import { useState, useEffect } from 'react';
import Customizable from '../images/icon-fully-customizable.svg';
import Detailed from '../images/icon-detailed-records.svg';
import Brand from '../images/icon-brand-recognition.svg';

const ShortenBox = () => {
    let api = 'https://api.shrtco.de/v2/shorten?url='
    const[value, setValue] = useState('');
    const [isPending, setIsPending] = useState(true)
    const [error, setError] = useState(null)
    const[copyText, setCopied] = useState(false);
    const [urlData, setUrlData] =useState([]);


    const fetchUrl = (url) =>{
        fetch(api + url).then(res => {
            if(!res.ok){
                throw Error('Could not fetch Data for that resource')
            }
            return res.json();
        })
        .then(data => {
            setIsPending(false);
            setUrlData(data);
            setError(null)
        })
        .catch(err =>{
            setIsPending(false);
            setError(err.message);
        })
        return{urlData, error, isPending}
    }

   
    const handleSubmit = () => {
        fetchUrl(value);
    }

    const copyToClipboard = (text) =>
        navigator.clipboard?.writeText && navigator.clipboard.writeText(text);

    // useEffect(() => {
    //     fetchUrl(value)
    // }, [value])

    const handleClick = (text) => {
        copyToClipboard(text)
        setTimeout(setCopied(true), 5000)
    }
   
    


    const cardData = [{
        id: 1,
        statsName: 'Brand Recongnition',
        info: ' Boost your brand recognition with each click. Generic links donâ€™t  mean a thing. Branded links help instil confidence in your content.',
        img: Brand
    },   
    {
        id:2,
        statsName: 'Detailed Records',
    info: 'Gain insights into who is clicking your links. Knowing when and where people engage with your content helps inform better decisions.',
    img: Detailed
        }, 
    {   
        id:3,
        statsName: 'Fully Customizable',
    info: 'Improve brand awareness and content discoverability through customizable links, supercharging audience engagement.',
    img: Customizable}]
  return (
    <section className='flex items-center space-y-10 flex-col bg-lightgray relative'>
        <div className="shortBox w-[70%] absolute back-desktop -top-8 flex flex-col md:flex-row items-center justify-center p-8 bg-DarkViolet rounded">
            <input onChange={(e) => {setValue(e.target.value)}} className='w-[80%] p-4 rounded ' type="text" placeholder='shorten a link here..' />
            <button onClick={handleSubmit} className='p-4 bg-Cyan m-2 font-bold hover:bg-Gray rounded text-White' >Shorten it</button>
        </div>

        <div className='p-16 w-[95%] md:p-20  md:w-[82%]'>
        
        { urlData && <div className='flex flex-col md:flex-row justify-between  bg-White rounded  p-8'> 
            <p>{urlData.original_link}</p>
            <span>{urlData.full_short_link2}</span>
            <button onClick={() => {handleClick(urlData.full_short_link2)}} className='text-slate-50 btn text-[13px] outline-none rounded bg-Cyan font-semibold text-White w-16 h-8'>{copyText == true ? 'Copied'  : 'Copy'  }</button>
            </div>}
        </div>
        

        <div className="statsbox flex space-y-5 flex-col m-5 p-12 items-center justify-center">
            <h3 className='text-4xl font-bold' >Advanced Statistics</h3>
            <p className='leading-6 w-[60%] font-semibold text-center '>Track how your Links are performing accross the web with our advanced statistics dashboard</p>
        </div>

       <div className="cards m-5 flex flex-col lg:flex-row  w-[70%]">
       {cardData.map((e) => {
            return(<div key={e.id} className="cards rounded space-y-5 relative bg-White text-DarkViolet p-8 m-5">
            <img className='absolute -top-6 left-7 bg-DarkViolet rounded-full p-2' src={e.img} alt="" />
            <h4 className='text-DarkViolet font-bold' >{e.statsName}</h4>
            <p className='text-Gray font-semibold' >{e.info}</p>
        </div>
        ) })}
       </div>
    </section>
  )
}

export default ShortenBox
